package week1.day1;

public class Conditional_SwitchCase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String Day = "Sunday";
		
		switch(Day) {
		
		case "Monday":
			System.out.println("Its not Holiday");
			break;
		case "Tuesday":
			System.out.println("Its not Holiday");
			break;
		case "Wednesday":
			System.out.println("Its not Holiday");
			break;
		case "Thursday":
			System.out.println("Its not Holiday");
			break;
		case "Friday":
			System.out.println("Its not Holiday");
			break;
		case "Saturday":
			System.out.println("Its not Holiday");
			break;
		case "Sunday":
			System.out.println("Its Holiday");
			break;
		default:
			System.out.println("Invalid Entry");
		
		}
		
	}

}
